CREATE TRIGGER `Notas_iniciales` AFTER INSERT ON `Paciente` FOR EACH ROW
begin
insert into Notas_medicas(No_Expediente, Nota) values(new.No_Expediente, "No hay notas aún");

insert into Sesiones_diarias(No_Expediente, Resumen, SesionCompleta, fechaSesion) values(new.No_Expediente, "No hay resumen de sesion aún", "No hay sesiones con el paciente", curdate());

end;;

