DROP TABLE IF EXISTS `Usuario`;
CREATE TABLE `Usuario` (
  `id_usuario` int NOT NULL,
  `usuario` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `contraseña` varchar(30) NOT NULL,
  `rol` int NOT NULL,
  `Sexo` int DEFAULT NULL,
  PRIMARY KEY (`id_usuario`),
  KEY `rol` (`rol`),
  KEY `Sexo` (`Sexo`),
  CONSTRAINT `Usuario_ibfk_1` FOREIGN KEY (`rol`) REFERENCES `Rol` (`id_rol`) ON DELETE CASCADE,
  CONSTRAINT `Usuario_ibfk_2` FOREIGN KEY (`Sexo`) REFERENCES `Sexo` (`Sexo`) ON DELETE RESTRICT
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

